// };

const PrivateRoute = (props) => {
	alert('here')
	return(<Route path="/dashboard" component={ props.isAuth ? props.component : <Redirect to="/" />}/>)
}

const App  = (props) => {
	const [isAuth, setAuth] = useState(false);
	const [user, setUser] = useState({});
	console.log(props.history)
	useEffect((props) =>
		firebase.auth().onAuthStateChanged((user) => {
			if (user) {
				setAuth(true);
				// history.push("/dashboard")
			}
		})
	);

	return (
		<ThemeProvider theme={lightTheme}>
			<CssBaseline />
			<Router>
				<Switch>
					<Route path="/dashboard" component={() => <Dashboard firebase={firebase}/>}  />
					<Route path="/" component={() => <Splash firebase={firebase}/>}/>
				</Switch>
			</Router>
		</ThemeProvider>
	);


	// return (
	// 	<ThemeProvider theme={lightTheme}>
	// 		<CssBaseline />
	// 		<Router>
	// 			<Switch>
	// 				<PrivateRoute
	// 					path="/dashboard"
	// 					isAuth={isAuth}
	// 					render={(props) => <Dashboard user={user} firebase={firebase} />}
	// 				/>
	// 				<Route path="/" render={(props) => <Splash firebase={firebase} />} />
	// 			</Switch>
	// 		</Router>
	// 	</ThemeProvider>
	// );
}

export default App;
