import React, { Component } from "react";
import {
    AppBar,
    Toolbar,
    Typography,
    IconButton,
    Drawer,
    List,
    ListItem,
    Fab,
} from "@material-ui/core";
import { Add, AccountCircle, Equalizer } from "@material-ui/icons";

import Problems from "./Problems.js";
import AddProblem from "./Add.js";
import Statistics from "./Statistics.js";
class Dashboard extends Component {
    constructor(props) {
        super(props);
        this.state = { statsOpen: false, database: {}, dialogOpen: false };
    }
    componentDidMount() {
        this.setState({ database: this.props.firebase.firestore() });
    }

    handleStats = () => {
        this.setState({ drawerOpen: !this.state.statsOpen });
    };

    handeDialog = () => {
        this.setState({ dialogOpen: !this.state.dialogOpen });
    };

    render() {
        return (
            <div>
                <AppBar position="relative" style={{ zIndex: "1400" }}>
                    <Toolbar>
                        <div style={{ marginRight: "auto" }}>
                            <IconButton onClick={this.handleStats}>
                                <Equalizer />
                            </IconButton>
                        </div>
                        <Typography variant="h5">IceBox</Typography>
                        <div style={{ marginLeft: "auto" }}>
                            <IconButton>
                                <AccountCircle />
                            </IconButton>
                        </div>
                    </Toolbar>
                </AppBar>
                <Problems mode="Edit" dialog={this.state.dialogOpen} onClick={this.handeDialog} />
                <div className="fab">
                    <Fab onClick={this.handeDialog} color="secondary">
                        <Add />
                    </Fab>
                </div>
                <Statistics open={this.state.statsOpen} onClose={this.handleStats}/>
                <footer>
                    <AddProblem
                        mode="New"
                        dialog={this.state.dialogOpen}
                        onClick={this.handeDialog}
                    />
                </footer>
            </div>
        );
    }
}

export default Dashboard;
