import React, { Component, useState } from "react";
import { Fab, Dialog, IconButton, DialogTitle, DialogContent, DialogActions, TextField, Button, FormControl } from "@material-ui/core";
import { Add } from "@material-ui/icons";
import firebase from "../firebase/firebase.js";

const db = firebase.firestore();

const AddProblem = (props) => {
	const [openDialog, setDialog] = useState(false)

	return (
		<div className="fab">
		<Fab>
			<IconButton onClick={() => (setDialog(!openDialog))}>
			<Add/>
			</IconButton>
		</Fab>
		<Dialog
			open={openDialog}
			fullWidth={true}
			maxWidth = {'md'}
			onClose={() => setDialog(!openDialog)}
		>
		<form>
		<DialogTitle> New Problem</DialogTitle>
			<TextField 
				fullWidth={true}
				multiline 
				required
				placeholder="Problem Statement"
				style={{'paddingLeft': '20pt','paddingRight': '20pt'}}/>
		<DialogActions>
			<Button type="submit" color="primary" variant="contained">Submit</Button>
		</DialogActions>
		</form>
		</Dialog>
		</div>
		)
} 

export default AddProblem
