import React, {useState} from "react";
import { Dialog, DialogTitle, DialogContent, DialogActions, Typography, TextField, Button } from "@material-ui/core";

const Profile = (props) => {
	const [name, setName] = useState(props.user.name)
	console.log(props.user)
	return (
		<Dialog open={props.open} onClose={props.onClose} fullWidth={true}>
			<DialogTitle>Profile Information</DialogTitle>
			<DialogContent>
				<Typography>Email: {props.user.email}</Typography>
				<TextField value={name} placeholder="Set your name!" variant="outlined" style={{margin: "3pt"}}/>
			</DialogContent>
			<DialogActions>
				<Button> Update Profile</Button>
			</DialogActions>
		</Dialog>
	);
};

export default Profile;
