import React, { Component } from 'react';
import {Card, CardContent, Typography, CardActions, Box, Chip, Avatar} from '@material-ui/core';

function ConvertDate (date) {
	var full_date = new Date(date.seconds*1000)
	return full_date.toLocaleDateString("en-US")
}

class ProblemCard extends Component {

	createChip = (category) => {
		var color_list = {A: "#ad1457", C: "#fdd835", G: "#00695c", N: "#0277bd", M: "#512da8"}
		return (
			<Chip
				avatar={<Avatar>category</Avatar>}
				color={color_list.category}
				variant="outlined"
			/>
			)
	}

	generateChips = (chip_array) => {

		var chips = []
		for (var i = 0; i < chip_array.length(); ++i){
			chips.push(this.createChip(chip_array[i]))
		}
		return(chips)
	}

	render () {
		console.log(this.props)
		return (
			<Card style={{minWidth: 1000, maxWidth: 1000}}>
				<CardContent>
					{this.props.statement}
				</CardContent>
				<CardActions>
					<div style={{margin: "10pt"}}>
						<table className="base_table">
						<tbody>
						<tr>
						<td className="left_col">
							{this.props.proposer} on {ConvertDate(this.props.date_created)}
							</td>
						<td className="center_col">{this.generateChips()}</td>
						<td className="right_col">Difficulty: {this.props.difficulty}</td>
						</tr>
						</tbody>
						</table>
					</div>
				</CardActions>
			</Card>
		);
	}
}

export default ProblemCard
