import React, { Component, useState } from "react";
import { Fab, Dialog, IconButton, DialogTitle, DialogContent, DialogActions, TextField } from "@material-ui/core";
import { Add } from "@material-ui/icons";
import firebase from "../firebase/firebase.js";

const db = firebase.firestore();

const AddProblem = (props) => {
	const [openDialog, setDialog] = useState(false)

	return (
		<Fab color="primary" >
			<IconButton onClick={() => (setDialog(!openDialog))}>
			<Add/>
			</IconButton>
			<Dialog
				open={openDialog}
				fullWidth={true}
				maxWidth = {'md'}
				onClose={() => setDialog(!openDialog)}
			>
			<DialogTitle> New Problem</DialogTitle>
			<DialogContent fullwidth={true}>
				<TextField 
					fullwidth={true} 
					multiline 
					placeholder="Problem Statement"
					style={{'margin': '10pt'}}/>
			</DialogContent>
			</Dialog>
		</Fab>
		)
} 

export default AddProblem
