import firebase from "../firebase/firebase.js";
var user = firebase.auth().currentUser;

const UpdateUser = (props) => {
	var user = firebase.auth().currentUser;
	console.log(props)
	user.updateProfile({
	})
		.then(function () {
			// Update successful.
		})
		.catch(function (error) {
			// An error happened.
		});
};

export default UpdateUser
