import React, { Component, useState } from "react";
import firebase from "./firebase/firebase.js";
import Splash from "./splash/Splash.js";
import Dashboard from "./dashboard/Dashboard.js";
import { createMuiTheme } from "@material-ui/core/styles";
import { BrowserRouter as Router, Route, Switch, useHistory } from "react-router-dom";
import "./css/Splash.css";
import "./css/Problem.css";
import { ThemeProvider, CssBaseline } from "@material-ui/core";

import { darkTheme, lightTheme } from "./css/Themes.js";

const PrivateRoute = ({ isAuth: isAuth, render: to_render, ...rest }) => {
	let history = useHistory()
	if (isAuth){
		history.push("/dashboard")
	}
	return <Route {...rest} render={isAuth === true ? to_render : null} />;
};

function App() {
	const [isAuth, setAuth] = useState(false);
	const [user, setUser] = useState({});

	// return (
	// 	<ThemeProvider theme={lightTheme}>
	// 		<CssBaseline />
	// 		<Router>
	// 			<Switch>
	// 				<Route path="/" render={(props) => <Dashboard firebase={firebase} />} />
	// 			</Switch>
	// 		</Router>
	// 	</ThemeProvider>
	// );

	firebase.auth().onAuthStateChanged((user) => {
		if (user) {
			setAuth(true);
			alert(JSON.stringify(user))
		}
	});

	return (
		<ThemeProvider theme={lightTheme}>
			<CssBaseline />
			<Router>
				<Switch>
					<PrivateRoute
						path="/dashboard"
						isAuth={isAuth}
						render={(props) => <Dashboard user={user} firebase={firebase} />}
					/>
					<Route path="/" render={(props) => <Splash firebase={firebase} />} />
				</Switch>
			</Router>
		</ThemeProvider>
	);
}

export default App;
