import React, { Component, useState, useEffect } from "react";
import {
    AppBar,
    Toolbar,
    Typography,
    IconButton,
    Drawer,
    List,
    ListItem,
    Fab,
} from "@material-ui/core";
import { Add, Menu, AccountCircle } from "@material-ui/icons";

import Problems from "./Problems.js";
import AddProblem from "./Add.js";
import Statistics from "./Statistics.js";
const Dashboard = (props) => {
    const [drawerOpen, handleDrawer] = useState(false);
    const [database, handleDatabase] = useState({});
    const [dialogOpen, handleDialog] = useState(false);

    useEffect(() => {
        handleDatabase(props.firebase.firestore());
    });
    return (
        <div>
            <AppBar position="relative" style={{ zIndex: "1400" }}>
                <Toolbar>
                    <div style={{ marginRight: "auto" }}>
                        <IconButton onClick={() => handleDrawer(!drawerOpen)}>
                            <Menu />
                        </IconButton>
                    </div>
                    <Typography variant="h5">IceBox</Typography>
                    <div style={{ marginLeft: "auto" }}>
                        <IconButton>
                            <AccountCircle />
                        </IconButton>
                    </div>
                </Toolbar>
            </AppBar>
            <Problems mode="Edit" dialog={dialogOpen} onClick={() => handeDialog(!dialogOpen)} />
            <div className="fab">
                <Fab onClick={() => handeDialog(!dialogOpen)} color="secondary">
                    <Add />
                </Fab>
            </div>
            <Drawer
                open={drawerOpen}
                onClose={() => handleDrawer(!drawerOpen)}
                anchor="left"
                variant="permanent"
            >
                <List style={{ marginTop: "100pt" }}>
                    <ListItem button>Test</ListItem>
                </List>
            </Drawer>
            <footer>
                <AddProblem mode="New" dialog={dialogOpen} onClick={handeDialog(!dialogOpen)} />
            </footer>
        </div>
    );
};

export default Dashboard;
