import React, { Component } from 'react';
import {Card, CardContent, Typography, CardActions, Box, Chip, Avatar} from '@material-ui/core';


function ConvertDate (date) {
	var full_date = new Date(date.seconds*1000)
	return full_date.toLocaleDateString("en-US")
}

function createChip (category) {
	var color_list = {A: "#ad1457", C: "#fdd835", G: "#00695c", N: "#0277bd", M: "#512da8"}
	var primary = "primary"
	return (
		<Chip
			avatar={<Avatar>{category}</Avatar>}
			style={{"backgroundColor": color_list[category]}}
		/>
		)
}

function generateChips (chip_array) {
	if (typeof chip_array !== 'undefined') {
		var chips = []
		for (var i = 0; i < chip_array.length; ++i){
			chips.push(createChip(chip_array[i]))
		}
		return(chips)}
	else {
		return (" ")
	}
}

const ProblemCard = (props) =>  {
	return (
		<Card style={{minWidth: 1000, maxWidth: 1000}} key={props.id}>
			<CardContent>
				{props.statement}
			</CardContent>
			<CardActions>
				<div style={{margin: "10pt"}}>
					<table className="base_table">
					<tbody>
					<tr>
					<td className="left_col">
						{props.proposer} on {ConvertDate(props.date_created)}
						</td>
					<td className="center_col">{generateChips(props.category)}</td>
					<td className="right_col">Difficulty: {props.difficulty}</td>
					</tr>
					</tbody>
					</table>
				</div>
			</CardActions>
		</Card>
	);
}

export default ProblemCard
